#Question 2

first_number = int(input("Enter the first number : "))                       #Asking user to give first number
second_number = int(input("Enter the second number : "))                     #Asking user to give second number
print(f"{first_number} + {second_number} = {first_number+second_number}")    #Performing Addition of first and second number
print(f"{first_number} - {second_number} = {first_number-second_number}")    #Performing Subtraction of first and second number
print(f"{first_number} * {second_number} = {first_number*second_number}")    #Performing Multiplication of first and second number
print(f"{first_number} / {second_number} = {first_number/second_number}")    #Performing Division of first and second number
print(f"{first_number} ^ {second_number} = {first_number**second_number}")   #Performing Power/Exponentiation of first and second number
print(f"{first_number} % {second_number} = {first_number%second_number}")    #Performing Modulus of first and second number